import React from 'react';
import constructor from './BurgerConstructor.module.css'
import {ConstructorElement, CurrencyIcon, Button} from '@ya.praktikum/react-developer-burger-ui-components'
import data from '../../utils/data'


class BurgerConstructor extends React.Component{
    render() {
        return(
            <div className={`mt-25 mr-4 ml-4 ${constructor.main}`}>
                <ul>
                <ConstructorElement
        type="top"
        isLocked={true}
        text="Краторная булка N-200i (верх)"
        price={20}
        thumbnail={'https://code.s3.yandex.net/react/code/bun-02.png'}
      />
      <ConstructorElement
        text="Соус традиционный галактический"
        price={30}
        thumbnail={"https://code.s3.yandex.net/react/code/sauce-03.png"}
      />
      <ConstructorElement
        text="Мясо бессмертных моллюсков Protostomia"
        price={300}
        thumbnail={"https://code.s3.yandex.net/react/code/meat-02.png"}
      />
        <ConstructorElement
        text="Плоды Фалленианского дерева"
        price={80}
        thumbnail={"https://code.s3.yandex.net/react/code/sp_1.png"}
      />
      <ConstructorElement
        text="Хрустящие минеральные кольца"
        price={80}
        thumbnail={"https://code.s3.yandex.net/react/code/mineral_rings.png"}
      />
      <ConstructorElement
        text="Хрустящие минеральные кольца"
        price={80}
        thumbnail={"https://code.s3.yandex.net/react/code/mineral_rings.png"}
      />
       <ConstructorElement
        type="button"
        isLocked={true}
        text="Краторная булка N-200i (верх)"
        price={20}
        thumbnail={'https://code.s3.yandex.net/react/code/bun-02.png'}
      />
      </ul>

                <div className={`mt-10 ${constructor.info}`}>
                    <div className={`mr-10 ${constructor.price}`}>
                    <p className={`text text_type_digits-medium ${constructor.counter}`}>610</p>
                    <CurrencyIcon type="primary" className={constructor.img} />
                    </div>

                <Button htmlType="button" type="primary" size="medium" >
Оформить заказ
</Button>



                </div>

            </div>
        )
    }
}

export default BurgerConstructor;
