import React from "react";
import PropTypes from 'prop-types'
import compound from '../IngredientDetails/IngredientDetails.module.css'



const Content = () => {
    const {content, children} = this.props;
    return (
        <div className={compound.box}>
            <p>{content} </p>

<span>{children}</span>

        </div>
    )
};






function IngredientDetails ({cardIngr}) {
    return (

        <div className={compound.container}>




                 <div className={`mt-10 ${compound.title}` } >

                    {/* <h1 className={` mb-8 text text_type_digits-large ${compound.text}`}> Детали ингредиентов </h1> */}
                    {/* <img src={image} className={'mb-15'}/> */}
                  </div>

                  <h1 className={` mb-15 text text_type_main-medium ${compound.subtitle}`}> {cardIngr.name} </h1>
                  <imag src={cardIngr.image_large} className={"mb-15"}/>
                  <div className={compound.details}>
                    <Content content="Калории, ккал"> {cardIngr.caloris}</Content>
                    <Content content="Белки,"> {cardIngr.proteins}</Content>
                    <Content content="Жиры,"> {cardIngr.fat}</Content>
                    <Content content="Углеводы,"> {cardIngr.carbohydates}</Content>

{/*                   
                  <p className={`mr-5 text text_type_main-default text_color_inactive ${compound.text}`}> Калории, ккал</p>
                  <p className={`mr-5 text text_type_main-default text_color_inactive ${compound.text}`}> Белки, г</p>
                  <p className={`mr-5 text text_type_main-default text_color_inactive ${compound.text}`}> Жиры, г</p>
                  <p className={`mr-5 text text_type_main-default text_color_inactive ${compound.text}`}> Углеводы, г</p> */}

                  </div>






        </div>

    )

}

IngredientDetails.propTypes = {
cardIngr: PropTypes.arrayOf(PropTypes.shape(
    {

       image_large: PropTypes.string.isRequired,
       name: PropTypes.string.isRequired,
       calories: PropTypes.number.isRequired,
       proteins: PropTypes.number.isRequired,
       fat: PropTypes.number.isRequired,
       carbohydrates: PropTypes.number.isRequired,

    }
  )
  )

};

export default IngredientDetails;
