    main: [],
    selected: [],
    load: false
});
  return (

    <>
      <AppHeader />
      <div className='page'>
        <main className="section">
          <BurgerIngredients setModalActive={setData}/>
          <BurgerConstructor setModalActive={setData}/>
          {/* <OrderDetail /> */}
        </main>
        <Modal active={modalActive} setActive={setModalActive}/> 
      </div>
    </>

  );
}

export default App;
