import React from 'react';
import ingredients from './BurgerIngredients.module.css';
import {Tab} from '@ya.praktikum/react-developer-burger-ui-components';
import {data }from '../../utils/data';

const Components = ({ data }) => {
    const image = (
      <img
        src={data.image}
      />
    );
    return (
      <div>
        <div className={'img'}>{image}</div>
        <p className={'description'}>{`${data.name}`}</p>
      </div>
    );
  };

class BurgerIngredients extends React.Component{
    render(){
       const {data} = this.state;
        return(
            <section className={ingredients.main}>
                <div className={` mt-10 mb-5 ${ingredients.container}`}>
                    <h1 className={`text text_type_main-medium ${ingredients.text}`}> Собери бургер </h1>

                </div>
                <div className={`mb-10 ${ingredients.tabs}`}>

                    <a href='/' className={ingredients.link}> 
                    <span className={`text text_type_main-default mt-4 mb-4 `}> Булки </span>
                    </a>
                    <a href='/' className={`text text_type_main-default ${ingredients.link}`}>
                        <span className={`mt-4 mb-4 ${ingredients.text}`}> 
                        Соусы
                        </span>
                    </a>
                    <a href='/' className={`text text_type_main-default ${ingredients.link}`}>
                        <span className={`pt-4 pb-4 ${ingredients.text}`}> Начинки </span>
                    </a>



                </div>
                <p className={`mb-6`}> Булки </p>
                <div className={` pl-4 pr-4 mb-10 ${ingredients.puns}`}>
                    <div className={`mr-4 ${ingredients.column}`}>
                        {data.map( (ingr, index) => <Components key={index} data={ingr}/>)}
                         </div>

                </div>
                <p className={`mb-6`}> Соусы </p>
                <div className={` pl-4 pr-4 mb-10 ${ingredients.souses}`}>
                    <div className={`mr-4 ${ingredients.column}`}>
                         </div>

                </div>

            </section>
        )
    }

}
export default BurgerIngredients;
