export default function Modal() {
    const { children, handleClose, onClose } = useState();


    // componentDidMount(){
    //     document.body.appendChild(this.el);
    // }

    // comonentWillUnmount(){
    //     document.body.removeChild(this.el);
    // }

    useEffect(() => {
        const closeOnEscapeKey = e => e.key === "Escape" ? handleClose() : null;
        document.body.addEventListener("keydown", closeOnEscapeKey);
        return () => {
            document.body.removeEventListener("keydown", closeOnEscapeKey);
        };
    }, [handleClose]);

    return ReactDOM.createPortal(


        <>

                <div className={`${stile.modal} ${children ? stile.open : ''}`}>
                    <div className={stile.title}>
                        <div className={stile.text}> Детали ингридиента </div>
                        <div className={stile.close} onClose={onClose}>
                            <CloseIcon type="primary" />
                        </div>
                    </div>
                    {children}
                </div>
                <ModalOverlay handleClose={handleClose}/>
        </>,



        document.getElementById('root-modal')
    )
}
