import React from 'react';
import ReactDOM from 'react-dom';
import stile from '../Modal/Modal.module.css'
import { CloseIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import ModalOverlay from "../ModalOverlay/ModalOverlay";


function Modal({children, setIsOpen, isOpen}) {

    const close = React.useCallback(() => {
        setIsOpen({
            isShow: false,
            title: null,
            content: null,
        }) 
    },[])

    return ReactDOM.createPortal(
        <>
        {/* <div className={`modal ${children ? "open" : ""}`}>  */}
        <div className={`${stile.modal1} ${children ? stile.open : ''}`}>

        <h1> hello</h1>
        <div className={stile.close} onClick={close}> 
        <CloseIcon type="primary"/>
        </div>
        </div>
        {/* <button className="close" onClick={() => setIsOpen(false)} >
            <CloseIcon type="primary"/>
       </button> */}


        {/* </div> */}
        {/* <div> 
            {children}
        </div> */}
        <ModalOverlay close={close} />

      </>  
    )
    // document.querySelector('#react-modals')
}


export default Modal;
