import React from "react";
import PropTypes from 'prop-types'
import compound from '../IngredientDetails/IngredientDetails.module.css'
import { cardIngr } from "../../utils/data";



const Content = (props) => {
    const {content, children} = props;
    return (
        <div>
            <p className={`mr-5 text text_type_main-default text_color_inactive ${compound.text}`}>  {content}</p>
            <span className="text text_type_digits-default">{children}</span>

        </div>
    )
};

function IngredientDetails (props) {
   const {data} = props;

    return (

        <div className={`pl-10 pb-15 pt-10 pr-10 ${compound.container}`}>

                 <img src={data.image_large} className={"mb-4"}/>
                  <h1 className={` mb-8 text text_type_main-medium ${compound.subtitle}`}> {data.name} </h1>


                  <ul className={compound.details}>
                    <li className={`mr-5 ${compound.li}`}> 
                    <Content content="Калории, ккал"> {data.caloris}</Content>
                    </li>
                    <li className={`mr-5 ${compound.li}`}>  
                    <Content content="Белки,"> {data.proteins}</Content>
                    </li>
                    <li className={`mr-5 ${compound.li}`}> 
                    <Content content="Жиры,"> {data.fat}</Content>
                    </li>
                    <li className={`mr-5 ${compound.li}`}> 
                    <Content content="Углеводы,"> {data.carbohydates}</Content>
                    </li>
                  </ul>
                  {/* <div className={compound.details}> */}






                  {/* <p className={`mr-5 text text_type_main-default text_color_inactive ${compound.text}`}> Калории, ккал</p>
                  <p className={`mr-5 text text_type_main-default text_color_inactive ${compound.text}`}> Белки, г</p>
                  <p className={`mr-5 text text_type_main-default text_color_inactive ${compound.text}`}> Жиры, г</p>
                  <p className={`mr-5 text text_type_main-default text_color_inactive ${compound.text}`}> Углеводы, г</p> */}

                  {/* </div>           */}
        </div>

    )

}

IngredientDetails.propTypes = {
   data: cardIngr.isRequired,


};

export default IngredientDetails;
