    return(
        <>
        <div className={`modal ${children ? "open" : ""}`}> 
        <button className="close" onClick={() => setIsOpen(false)} >
            <CloseIcon type="primary"/>
       </button>
       <h1> hello</h1>

        </div>
        {/* <div> 
