import React, { useCallback } from 'react';
import ReactDOM from 'react-dom';
import stile from '../Modal/Modal.module.css'
import { CloseIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import ModalOverlay from "../ModalOverlay/ModalOverlay";
import { useEffect, useState } from 'react';
import { createPortal } from 'react-dom/client';
import clsx from 'clsx'





export default function Modal({ children, onClose, header }) {








    const closeOnEscapeKey = e => e.key === "Escape" ? onClose() : null;
    useEffect(() => {
        document.addEventListener("keydown", closeOnEscapeKey);
        return () => {
            document.removeEventListener("keydown", closeOnEscapeKey);
        };
    }, [onClose]);

    return ReactDOM.createPortal(


        <>

            {/* <div className={`${stile.modal} ${children ? stile.open : '' }`}> */}
            <ModalOverlay onClose={onClose} />
            <div className={clsx(stile.modal, "pl-10 pr-10 pt-10")} onClick={onClose}>
                <div className={stile.title}>
                    <div className={stile.text} text text_type_main-large>
                        {header}
                        <div className={stile.close} onClick={onClose}>
                            <CloseIcon type="primary" />
                        </div>

                    </div>

                </div>
                {children}
            </div>

        </>,



        document.getElementById('root-modal')
    )
}
