import React from "react";
import PropTypes from 'prop-types'
import compound from '../IngredientDetails/IngredientDetails.module.css'
import close from  '../../images/close.svg'


const Content = () => {
    const {content, children} = this.props;
    return (
        <div className={compound.box}>
            <p>{content} </p>

<span>{children}</span>

        </div>
    )
};

Content.propTypes = {
    content: PropTypes.string,
    children: PropTypes.number
}


function Ingredient ({image, name}) {
    return (

        <div className={compound.container}>




                 <div className={`mt-10 ${compound.title}` } >

                    <h1 className={` mb-8 text text_type_digits-large ${compound.text}`}> Детали ингредиентов </h1>
                    <img src={close} className={compound.image}/>
                  </div>

                  <h1 className={` mb-15 text text_type_main-medium ${compound.subtitle}`}> {name} </h1>
                  <imag src={image} className={"mb-15"}/>
                  <div className={compound.details}>

                  <p className={`mr-5 text text_type_main-default text_color_inactive ${compound.text}`}> Калории, ккал</p>
                  <p className={`mr-5 text text_type_main-default text_color_inactive ${compound.text}`}> Белки, г</p>
                  <p className={`mr-5 text text_type_main-default text_color_inactive ${compound.text}`}> Жиры, г</p>
                  <p className={`mr-5 text text_type_main-default text_color_inactive ${compound.text}`}> Углеводы, г</p>
                  </div>






        </div>

    )

}



export default Ingredient;
